  //   name: "State of a component",
  //   exercises: 14
  // }

  // Header Component
  const Header = (props) => {
    return (
      <div>
        <h1>{props.course}</h1>
      </div>
    );
  }

  // It shows parts of content component
  const Part = (props) => {
    console.log('INSIDE PART COMP: ', props);
    return (
      <p>
        {/* {props.part.name} {props.part.exercises} */}
      </p>
    )
  } 

  // Content Component
  const Content = (props) => {
    console.log('INSIDE CONTENT: ', props.parts[0]);
    return (
      <div>
        <Part part={props[0]} />
        {/* <Part part={props[1]} />
        <Part part={props[2]} /> */}
      </div>
    )
  }

  // Total component
  const Total = (props) => {
    return (
      <p>
        Number of exercises {props.exercises}
      </p>
    )
  }

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      {/* <Content part={part1} exercises={exercises1} />
