import { useState } from "react";


const Statistics = ({good, neutral, bad, averageScore}) => {
  const totalFeedback = good+bad+neutral
  return (
    <div>
    <h1>statistics</h1>
    <p>good {good}</p>
    <p>neutral {neutral}</p>
    <p>bad {bad}</p>
    <p>all {totalFeedback}</p>
    <p>average {averageScore/totalFeedback}</p>
    <p>positive {good/totalFeedback}</p>
  </div>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [averageScore, setAverageScore] = useState(0)

  const handleGoodClick = () => {
    setGood(good + 1)
    setAverageScore(averageScore+1)
  }

  const handleNeutralClick = () => {
    setNeutral(neutral + 1)
    setAverageScore(averageScore+0)
  }

  const handleBadClick = () => {
    setBad(bad + 1)
    setAverageScore(averageScore-1)
  }

  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={handleGoodClick}>good</button>
      <button onClick={handleNeutralClick}>neutral</button>
      <button onClick={handleBadClick}>bad</button>

      <Statistics good={good} neutral={neutral} bad={bad} averageScore={averageScore}  />
    </div>
  )
}

export default App;
